import React, {useState} from 'react';
import ExpenseItem from "./ExpenseItem";
import ExpenseFilter from "../ExpenseFilter/ExpenseFilter";
import "./Expenses.css";
import Card from "../UI/Card"

const Expenses = (props) => {

    const [filterYear, setFilterYear] = useState('2022');

    const filterChangeHandler = (selectedFilterYear) => {
        console.log("Expenses.js:", selectedFilterYear);
        setFilterYear(selectedFilterYear);
    }


    return(
        <div>
            <ExpenseFilter defaultYear={filterYear} onFilterChange={filterChangeHandler}/>
            <Card className="expenses">
                <h2>Expense Tracker</h2>

                {props.expenseList.map(expense=>
                    <ExpenseItem
                        title={expense.title}    
                    />)}

                <ExpenseItem
                    title={props.expenseList[0].title}
                    price={props.expenseList[0].price}
                    date={props.expenseList[0].date}
                />

                <ExpenseItem
                    title={props.expenseList[1].title}
                    price={props.expenseList[1].price}
                    date={props.expenseList[1].date}
                />

                <ExpenseItem
                    title={props.expenseList[2].title}
                    price={props.expenseList[2].price}
                    date={props.expenseList[2].date}
                />

                <ExpenseItem
                    title={props.expenseList[3].title}
                    price={props.expenseList[3].price}
                    date={props.expenseList[3].date}
                />

            </Card>
        </div>
    )
}

export default Expenses
