import React from 'react';
import './ExpensesList.css';
import ExpenseItem from './ExpenseItem';

const ExpensesList = (props) => {

    let expensesContent = (<p className="error">No expenses found</p>)
    let expenseList = props.items;

    if (props.items.length > 0){
        expensesContent = ( // override the default if there's content in the list
            expenseList.map((expense) =>   
                <ExpenseItem
                    key={expense.id}
                    title={expense.title}
                    price={expense.price}
                    date={expense.date} 
                />
        ));
    }

    return(
        <ul className="expenses-list"></ul>
    )
}

export default ExpensesList;
