import React, {useState} from 'react';
import ExpenseItem from "./ExpenseItem";
import ExpenseFilter from "../ExpenseFilter/ExpenseFilter";
import "./Expenses.css";
import Card from "../UI/Card"

const Expenses = (props) => {

    const [filterYear, setFilterYear] = useState('2022');
    const [expenseList, setExpenseList] = useState(props.expenseList);

    console.log("Current Expense List:", expenseList);

    const filterChangeHandler = (selectedFilterYear) => {
        setFilterYear(selectedFilterYear);
        const filteredExpenses = props.expenseList.filter(item => item.date.getFullYear() === parseInt(filterYear));
        setExpenseList(filteredExpenses);
    }

    let expensesContent = <p className="error">No expenses found</p> // default value

    if (expenseList > 0){
        expensesContent = (
            expenseList.map((expense) =>   
                        <ExpenseItem
                            key={expense.id}
                            title={expense.title}
                            price={expense.price}
                            date={expense.date} 
                        />
                    )
        )
    }

    return(
        <div>
            <ExpenseFilter defaultYear={filterYear} onFilterChange={filterChangeHandler}/>
            <Card className="expenses">
                <h2>Expense Tracker</h2>

                {expenseList.length === 0 && <p className="error">No expenses found</p> }

                {expenseList.length > 0 && 
                    expenseList.map((expense) =>   
                        <ExpenseItem
                            key={expense.id}
                            title={expense.title}
                            price={expense.price}
                            date={expense.date} 
                        />
                    )
                }



            </Card>
        </div>
    )
}

export default Expenses
